export async function getArticlesBySubtype() {
  const response = await fetch("https://api-test-ln.herokuapp.com/articles");
  const responseJson = await response
    .json()
    .then(response => response.json())
    .then(articlesJson =>
      this.setState({ articles: articlesJson.articles, isFetch: false })
    );
}
export default {
  getArticlesBySubtype
};
