import React from "react";
import Article from "./Article";
import { getArticles } from "../services/articles";

class ArticlesContainer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      articles: [],
      isFetch: true
    };
  }

  async componentDidMount() {
    const responseJson = await getArticles();
    this.setState({ articles: responseJson.articles, isFetch: false });
  }

  render() {
    const { isFetch, articles } = this.state;
    const articlesSub7 = articles.filter(articles => article.subtype === 7);

    if (isFetch) {
      return "Loading";
    }
    return articles.map(articlesSub7 => (
      // <Article {...article}></Article> // se le pasan las props del objeto article, al componente article

      <Article
        name={articlesSub7.headlines.basic}
        // imgUrl={articles.promo_items}
        key={articlesSub7._id}
        subtype={articlesSub7.subtype}
      ></Article>
    ));
  }
}

export default ArticlesContainer;
