import React from "react";
import Article from "./Article";

class ArticlesContainer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      articles: [],
      isFetch: true
    };
  }

  componentDidMount() {
    fetch("https://api-test-ln.herokuapp.com/articles")
      .then(response => response.json())
      .then(articlesJson => this.setState({ articles: articlesJson.articles }));
  }

  render() {
    const name = this.state.articles[0].display_date;
    return <Article name="asdasd"></Article>;
  }
}

export default ArticlesContainer;
