import React from "react";
import Article from "./Article";

class ArticlesContainer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      articles: [],
      isFetch: true
    };
  }

  componentDidMount() {
    fetch("https://api-test-ln.herokuapp.com/articles")
      .then(response => response.json())
      .then(articlesJson =>
        this.setState({ articles: articlesJson.articles, isFetch: false })
      );
  }

  render() {
    if (this.state.isFetch) {
      return "Loading";
    }
    // const name = this.state.articles[0].headlines.basic;
    return this.state.articles.map(article => <Article name={name}></Article>);
  }
}

export default ArticlesContainer;
