import React from "react";
import Article from "./Article";
import { getArticlesBySubtype } from "../services/articles";

class ArticlesContainer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      articles: [],
      isFetch: true
    };
  }

  componentDidMount() {
    const responseJson = getArticlesBySubtype();
    console.log(responseJson);
  }

  render() {
    const { isFetch, memes } = this.state;

    if (isFetch) {
      return "Loading";
    }
    // const name = this.state.articles[0].headlines.basic;
    return this.state.articles.map(article => (
      <Article name={article.headlines.basic} key={article._id}></Article>
    ));
  }
}

export default ArticlesContainer;
