import React from "react";
import Article from "./Article";
import { getArticlesBySubtype } from "../services/articles";

class ArticlesContainer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      articles: [],
      isFetch: true
    };
  }

  async componentDidMount() {
    const responseJson = await getArticlesBySubtype();
    this.setState({ articles: responseJson.articles, isFetch: false });
  }

  render() {
    const { isFetch, articles } = this.state;

    if (isFetch) {
      return "Loading";
    }
    return articles.map(article => (
      <Article {...article.promo_items}></Article> // se le pasan las props del objeto article, al componente article

      //   <Article
      //     name={article.headlines.basic}
      //     imgUrl={article.promo_items.basic}
      //     key={article._id}
      //   ></Article>
    ));
  }
}

export default ArticlesContainer;
